import React, { Component } from 'react';

export default class Map extends Component{
  constructor() {
    super();
    this.setLatLong = this.setLatLong.bind(this);
    this.state = {
      el: '',
      daumMap: '',
      marker: '',
      latSet: [],
      longSet: []
    }
  }

  componentDidMount() {
    let el = document.getElementById('map');
    let daumMap = new window.daum.maps.Map(el, {
      center: new window.daum.maps.LatLng(35.56, 128.12),
      level: 13
    });

    let marker = new window.daum.maps.Marker({
      map: this.state.daumMap,
      position: daumMap.getCenter()
    });

    this.setState({
      el, daumMap, marker
    });

    // marker.setMap(this.state.daumMap);

    console.log(this.props, 'props');
    var imageSrc = "http://t1.daumcdn.net/localimg/localimages/07/mapapidoc/markerStar.png"; 

  }

  setLatLong(obj) {
    const { daumMap } = this.state;
    console.log(obj, 'helllllll');
    let i = 0;
    let latList = new Array();
    let longList = new Array();
    let txtList = new Array();
    let reportedAtList = new Array();
    while (obj[i] !== undefined) {
      console.log(obj[i], 'obj[i]');
      console.log(obj[i].latitude, 'obj[i].latitude');
      console.log(obj[i].longitude, 'obj[i].longitude');
      latList[i] = obj[i].latitude;
      longList[i] = obj[i].longitude;
      txtList[i] = obj[i].description;
      reportedAtList[i] = obj[i].reportedAt;
      i++;
    }

    console.log(latList, 'latList');
    console.log(latList.length, 'latList.length');

    var imageSrc = "http://t1.daumcdn.net/localimg/localimages/07/mapapidoc/markerStar.png"; 

    for (var idx = 0; idx < latList.length; idx++) {
      var imageSize = new window.daum.maps.Size(24, 35);

      var markerImage = new window.daum.maps.MarkerImage(imageSrc, imageSize);

      //TODO : latitude , longitude 단어 헷갈 ;;; 뭔가 꼬임 
      var marker = new window.daum.maps.Marker({
        map: daumMap,
        position: new window.daum.maps.LatLng(longList[idx], latList[idx]),
        title: `"${txtList[idx]}" -${reportedAtList[idx]}`,
        image: markerImage
      })
    }

  }

  render() {
    const mapStyle = {
      width: '70%',
      height: '500px',
      overflow: 'hidden',
      margin: '15px 0',
    }

    const { latSet, longSet } = this.state;

    console.log(latSet, 'LAT SET');
    return (
      <div style={{display: 'flex', alignItems: 'center', justifyContent: 'center', flexDirection: 'column', marginBottom: '30px'}}>
        <h2>목격된 장소</h2>
        <div id="map" style={mapStyle}>
        </div>
      </div>
    );  
  }

}
