import React, { Component } from 'react';

export default class Map extends Component{
  constructor() {
    super();
    this.setLatLong = this.setLatLong.bind(this);
    this.state = {
      el: '',
      daumMap: '',
      marker: '',
      latSet: [],
      longSet: []
    }
  }

  componentDidMount() {
    let el = document.getElementById('map');
    let daumMap = new window.daum.maps.Map(el, {
      center: new window.daum.maps.LatLng(35.56, 128.12),
      level: 13
    });

    let marker = new window.daum.maps.Marker({
      map: this.state.daumMap,
      position: daumMap.getCenter()
    });

    this.setState({
      el, daumMap, marker
    });

    // marker.setMap(this.state.daumMap);

    console.log(this.props, 'props');
    var imageSrc = "http://t1.daumcdn.net/localimg/localimages/07/mapapidoc/markerStar.png"; 

  }

  render() {
    const mapStyle = {
      width: '70%',
      height: '500px',
      overflow: 'hidden',
      margin: '15px 0',
    }

    const { latSet, longSet } = this.state;

    console.log(latSet, 'LAT SET');
    return (
      <div style={{display: 'flex', alignItems: 'center', justifyContent: 'center', flexDirection: 'column', marginBottom: '30px'}}>
        <h2>목격된 장소</h2>
        <div id="map" style={mapStyle}>
        </div>
      </div>
    );  
  }

}
